import React from 'react';
import NextHead from 'next/head';
import { string } from 'prop-types';
import renderHTML from 'react-render-html';

const defaultOGURL = '';

const Head = ({ yoast, featuredImage, title, options }) => (
  <NextHead>
    <meta charSet="UTF-8" />
    <title>{renderHTML(yoast.yoast_wpseo_title || title)}</title>
    <meta
      name="description"
      content={yoast.yoast_wpseo_metadesc || (yoast.meta ? yoast.meta.description : '')}
    />
    <link rel="canonical" href={yoast.yoast_wpseo_canonical || ''} />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    {/* <link rel="apple-touch-icon" href="/static/icons/apple-touch-icon.png" /> */}
    <link rel="mask-icon" href="/static/favicon-mask.svg" color="#49B882" />
    <link rel="icon" href="/static/favicon.ico" />
    {/* <link rel="icon" type="image/png" sizes="32x32" href="/static/icons/favicon-32x32.png" /> */}
    {/* <link rel="icon" type="image/png" sizes="16x16" href="/static/icons/favicon-16x16.png" /> */}
    {/* <link
      rel="icon"
      type="image/png"
      sizes="192x192"
      href="/static/icons/android-chrome-192x192.png"
    /> */}
    {/* <link
      rel="icon"
      type="image/png"
      sizes="256x256"
      href="/static/icons/android-chrome-256x256.png"
    /> */}
    <meta property="og:url" content={yoast.yoast_wpseo_canonical || defaultOGURL} />
    <meta
      property="og:title"
      content={yoast.meta ? yoast.meta.og_title : '' || yoast.yoast_wpseo_title}
    />
    <meta
      property="og:description"
      content={yoast.meta ? yoast.meta.og_description : '' || yoast.yoast_wpseo_metadesc}
    />
    <meta name="twitter:site" content={yoast.yoast_wpseo_canonical || defaultOGURL} />
    <meta name="twitter:card" content="summary_large_image" />
    <meta
      name="twitter:image"
      content={
        yoast.meta && yoast.meta.twitter_image
          ? yoast.meta.twitter_image
          : featuredImage
          ? featuredImage.source_url
          : ''
      }
    />
    <meta
      name="og:image"
      content={
        yoast.meta && yoast.meta.og_image
          ? yoast.meta.og_image
          : featuredImage
          ? featuredImage.source_url
          : ''
      }
    />
    <meta property="og:image:width" content="1200" />
    <meta property="og:image:height" content="630" />
    <link
      href="https://fonts.googleapis.com/css?family=Abril+Fatface|Work+Sans:400,500,700"
      rel="stylesheet"
    />
    {/* <link
      rel="stylesheet"
      href="https://cdnjs.cloudflare.com/ajax/libs/Swiper/4.4.1/css/swiper.min.css"
    /> */}
    {options && options.acf && options.acf.analytics_id && (
      <React.Fragment>
        <script
          async
          src={`https://www.googletagmanager.com/gtag/js?id=${options.acf.analytics_id}`}
        />
        <script
          dangerouslySetInnerHTML={{
            __html: `
            window.dataLayer = window.dataLayer || [];
            function gtag(){dataLayer.push(arguments);}
            gtag('js', new Date());
            gtag('config', '${options.acf.analytics_id}');
          `,
          }}
        />
      </React.Fragment>
    )}
  </NextHead>
);

Head.propTypes = {
  title: string,
  description: string,
  url: string,
  ogImage: string,
};

export default Head;
