import styled from 'styled-components';
import { Row } from '../../styles';
import { px2rem } from '../../../utils';

const STilesFeatured = styled(Row)`
  .small-tiles {
    max-height: ${px2rem(500)};

    > div {
      height: 100%;
    }
  }
`;

export default STilesFeatured;
