import styled, { css } from 'styled-components';

const StyledIcon = styled.svg`
  flex: none;
  transition: fill 0.25s;

  &:hover {
    fill: ${props => (props.hover ? props.hover : '')};
  }

  ${props => (props.rotate
    ? css`
          transform: rotate(${props.rotate}deg);
        `
    : css``)}
`;

export default StyledIcon;
