import React from 'react';
import PropTypes from 'prop-types';

// Styling
import { Flex, FormLabel } from '../../styles';
import STextInput, { SError } from './styles';

const Email = ({
  label,
  id,
  name,
  isRequired,
  errorMessage,
  width,
  register,
  errors,
  ...props
}) => {
  return (
    <Flex mb={[15, 30]} mt={[15, 0]} px={[0, 25]} flexDirection="column" width={width}>
      {label && (
        <Flex alignItems="center">
          <FormLabel htmlFor={id}>{isRequired ? label + '*' : label}</FormLabel>
        </Flex>
      )}
      <STextInput
        id={id}
        name={name}
        ref={register({
          required: 'Required',
          pattern: {
            value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i,
            message: 'invalid email address',
          },
        })}
        {...props}
      />
      {errors?.email && <SError>{errorMessage.required}</SError>}
      {errors && <SError>{errorMessage.email}</SError>}
    </Flex>
  );
};

Email.propTypes = {
  label: PropTypes.string,
  id: PropTypes.string.isRequired,
  isRequired: PropTypes.bool,
  errorMessage: PropTypes.object,
  width: PropTypes.oneOfType([PropTypes.number, PropTypes.array]),
  register: PropTypes.func.isRequired,
  errors: PropTypes.oneOfType([PropTypes.object, PropTypes.bool]),
};

Email.defaultProps = {
  label: '',
  isRequired: false,
  errorMessage: {},
  width: [1, 1 / 2],
  errors: false,
};

export default Email;
