import styled, { css } from 'styled-components';
import { Box, Text } from '../../styles';
import { px2rem } from '../../../utils';
import theme from '../../../theme';

const SMenu = styled(Box)`
  position: fixed;
  top: 0;
  left: 0;
  padding: ${px2rem(20)};
  width: 100vw;
  height: 100vh;
  z-index: 98;
  //opacity: 0;
  //transform: scale(1.05);
  //pointer-events: none;
  background: ${theme.colors.white};
  transition: opacity 0.3s ease, transform 0.3s ease;

  > div {
    height: 100%;
  }

  .menu-wrapper {
    position: relative;
    height: 100%;
    overflow-y: scroll;

    &:after {
      content: '';
      position: fixed;
      bottom: 20px;
      right: 0;
      height: ${px2rem(125)};
      width: 100%;
      background: linear-gradient(to top, rgba(14, 14, 14, 0.99) 65%, rgba(14, 14, 14, 0));
    }
  }

  ${props =>
    props.isOpen &&
    css`
      opacity: 1;
      transform: scale(1);
      pointer-events: initial;
    `}
`;

export const SMenuButton = styled(Text)`
  position: relative;
  z-index: 99;
  padding-right: ${px2rem(40)};
  background: 0;
  border: 0;
  box-shadow: none;
  font-family: ${theme.fonts.heading};
  cursor: pointer;

  &:before,
  &:after {
    content: '';
    position: absolute;
    right: 0;
    width: ${px2rem(20)};
    height: ${px2rem(2)};
    background: ${theme.colors.black};
    transition: transform 0.3s ease;
  }

  &:before {
    top: ${px2rem(5)};

    ${props =>
      props.openMenu &&
      css`
        top: ${px2rem(8)};
        transform: rotate(-45deg);
        background: ${theme.colors.white};
        transition: transform 0.3s ease;
      `}
  }

  &:after {
    top: ${px2rem(12)};

    ${props =>
      props.openMenu &&
      css`
        top: ${px2rem(8)};
        transform: rotate(45deg);
        background: ${theme.colors.white};
        transition: transform 0.3s ease;
      `}
  }

  span {
    opacity: ${props => (props.openMenu ? 0 : 1)};
  }
`;

export const MenuItem = styled(Text)`
  border-bottom: 1px solid ${theme.colors.menuGray};
  font-family: ${theme.fonts.heading};
  text-transform: uppercase;
  text-align: right;
  list-style: none;
  line-height: 0.9;
`;

export default SMenu;
