import React from 'react';
import PropTypes from 'prop-types';

// Styling
import { Text } from '../styles';
import STile from './styles';
import theme from '../../theme';

const Tile = ({ title, client, image, link, isSmall, isLast }) => (
  <STile>
    <a href={link} target="_blank">
      {image && !isSmall && <img src={`../../../static/images/${image}`} />}
      {client && (
        <Text as="span" mt={20} fontSize={15} fontWeight={700} color={theme.colors.gray}>
          {client}
        </Text>
      )}
      <Text
        as="h3"
        mt={10}
        fontSize={!isSmall ? 30 : 24}
        fontWeight={400}
        color={theme.colors.black}
      >
        {title}
      </Text>
    </a>
  </STile>
);

Tile.propTypes = {
  title: PropTypes.string.isRequired,
  client: PropTypes.string,
  image: PropTypes.string,
  link: PropTypes.string,
  isSmall: PropTypes.bool,
  isLast: PropTypes.bool,
};

Tile.defaultProps = {
  client: '',
  image: '',
  link: '',
  isSmall: false,
  isLast: false,
};

export default Tile;
