import React from 'react';
import PropTypes from 'prop-types';

// Utils
import { get } from '../api/callers';

// Components
import CasesOverview from '../components/Templates/CasesOverview';

const Home = ({ layouts }) => <CasesOverview layouts={layouts} />;

Home.getInitialProps = async () => {
  const cases = get('cases/').then(resp => resp?.layouts);
  const clients = get('clients/');

  const layouts = Promise.all([cases, clients]).then(values => [...values[0], ...values[1]]);

  console.log(layouts);

  return { layouts };
};

Home.propTypes = {
  layouts: PropTypes.arrayOf(PropTypes.object),
};

Home.defaultProps = {
  layouts: [],
};

export default Home;
