import React from 'react';
import PropTypes from 'prop-types';

// Components
import Tile from '../../Tile';

// Styling
import { Box, Flex } from '../../styles';

const Tiles = ({ items }) => (
  <Flex flexWrap="wrap">
    {items?.map(item => (
      <Box width={1 / 2}>
        <Tile {...item} />
      </Box>
    ))}
  </Flex>
);

Tiles.propTypes = {
  items: PropTypes.arrayOf(PropTypes.object).isRequired,
};

export default Tiles;
