import React from 'react';
import PropTypes from 'prop-types';
import uuid from 'uuid';

// Components
import Tile from '../../Tile';

// Styling
import { Box, Flex, Row } from '../../styles';

const TilesFeatured = ({ items }) => {
  const featuredItem = items?.findIndex(item => item.image);
  const smallTiles = items.filter(item => !item.image);

  console.log(smallTiles);

  return (
    <Row>
      <Flex mx={-15} flexWrap="wrap">
        {smallTiles?.map(item => (
          <Box key={uuid()} my={40} px={15} width={1 / 2}>
            <Tile isSmall {...item} />
          </Box>
        ))}
      </Flex>
    </Row>
  );
};

TilesFeatured.propTypes = {
  items: PropTypes.arrayOf(PropTypes.object).isRequired,
};

export default TilesFeatured;
