import styled, { css } from 'styled-components';
import { px2rem } from '../../utils';
import { Button } from '../styles';

const SCookieBar = styled.div`
  position: fixed;
  z-index: 1001;
  bottom: 0;
  left: 0%;
  width: 100%;
  transform: translateY(100%);
  transition: transform 0.3s;
  visibility: hidden;
  ${(props) =>
    props.showCookie &&
    css`
      transform: translateY(0%);
      visibility: visible;
    `}

  > div {
    padding: ${px2rem(6, 10)};
    text-align: center;

    a {
      text-decoration: underline;
    }
  }
  ${Button} {
    font-size: 15px;
    font-weight: normal;
    padding: ${px2rem(2, 10)};
    line-height: 1.3;
  }

  p {
    margin: 0%;
    font-size: ${px2rem(13)};
  }
`;

export default SCookieBar;
