import styled from 'styled-components';
import { Text } from '../styles';
import { px2rem, media } from '../../utils';

const Wysiwyg = styled(Text)`
  p {
    font-size: ${px2rem(16)};
    line-height: 1.5;
  }
  .button {
    position: relative;
    display: inline-block;
    cursor: pointer;
    :hover {
    }
  }

  ul,
  ol {
    padding-left: ${px2rem(20)};
    line-height: 1.6;
    list-style: none;
    > li {
      &:before {
        content: 'â€¢';
        display: inline-block;
        width: ${px2rem(20)};
        margin-left: ${px2rem(-20)};
        vertical-align: baseline;
        font-size: ${px2rem(18)};
      }
    }
  }

  ol {
    counter-reset: item;
    > li {
      &:before {
        content: counter(item);
        counter-increment: item;
      }
    }
  }

  a {
    text-decoration: underline;
    &:hover {
      text-decoration: none;
    }
  }

  h1 {
    font: normal bold ${px2rem(40)} / ${px2rem(46)} ${(props) => props.theme.fonts.heading};
    margin-bottom: ${px2rem(22)};
    ${media.small`
      font: normal bold ${px2rem(56)}/${px2rem(62)} ${(props) => props.theme.fonts.heading};
    `}
  }
  h2 {
    margin: ${px2rem(40, 0, 20)};
    font: normal bold ${px2rem(32)} / ${px2rem(38)} ${(props) => props.theme.fonts.heading};
    ${media.small`
      font: normal bold ${px2rem(40)}/${px2rem(46)} ${(props) => props.theme.fonts.heading};
    `}
  }
  h3 {
    margin: ${px2rem(40, 0, 16)};
    font: normal bold ${px2rem(24)} / ${px2rem(30)} ${(props) => props.theme.fonts.heading};
    ${media.small`
      font: normal bold ${px2rem(32)}/${px2rem(38)} ${(props) => props.theme.fonts.heading};
    `}
  }
  h4 {
    margin: ${px2rem(40, 0, 16)};
    font: bold ${px2rem(16)} / ${px2rem(22)} ${(props) => props.theme.fonts.body};
    ${media.small`
      font: bold ${px2rem(24)}/${px2rem(30)} ${(props) => props.theme.fonts.body};
    `}
  }
  h5 {
    margin: ${px2rem(40, 0, 16)};
    font: bold ${px2rem(16)} / ${px2rem(22)} ${(props) => props.theme.fonts.body};
    ${media.small`
      font: bold ${px2rem(18)}/${px2rem(30)} ${(props) => props.theme.fonts.body};
    `}
  }
  h6 {
    margin: ${px2rem(40, 0, 12)};
    font: bold ${px2rem(15)} / ${px2rem(21)} ${(props) => props.theme.fonts.body};
    ${media.small`
      font: bold ${px2rem(16)}/${px2rem(24)} ${(props) => props.theme.fonts.body};
    `}
  }

  ${media.small`
    p{
      font-size: ${px2rem(18)};
      line-height: 1.6;
    }
    ul, ol{
      font-size: ${px2rem(18)};
    }
  `}
`;

export default Wysiwyg;
