export const breakpoints = [640, 1024, 1440];

export default {
  gridSize: '1440px',
  breakpoints: breakpoints.map(i => i + 'px'),
  fontSizes: [12, 14, 16, 20, 24, 32, 48, 64],
  colors: {
    white: '#fff',
    black: '#000',
    gray: '#9e9e9e',
    lightgray: '#f6f6ff',
    blue: '#07c',
    red: '#f44336',
  },
  space: [0, 4, 8, 16, 32, 64, 128, 256],
  fonts: {
    heading: 'system-ui, sans-serif',
    body: 'system-ui, sans-serif',
  },
};
