import React from 'react';
import PropTypes from 'prop-types';
import Link from 'next/link';
import uuid from 'uuid';

// Styling
import { Box, Flex, Text, Row } from '../styles';
import SFooter, { SFooterMenu } from './styles';
import theme from '../../theme';

const Footer = ({ menu, title }) => (
  <SFooter as="footer" py={100} bg={theme.colors.black}>
    <Row px={0}>
      <Flex alignItems="center">
        <Box width={3 / 12}>
          <Link href="/">
            <a>
              <img src="../../static/logoWhite.svg" alt={title} />
            </a>
          </Link>
        </Box>
        {menu && (
          <SFooterMenu as="ul" width={6 / 12}>
            {menu.map(item => (
              <Box key={uuid()} as="li">
                <Text as="a" href={item.url} target={item.target} color={theme.colors.white}>
                  {item.title}
                </Text>
              </Box>
            ))}
          </SFooterMenu>
        )}
      </Flex>
    </Row>
  </SFooter>
);

Footer.propTypes = {
  title: PropTypes.string.isRequired,
  menu: PropTypes.arrayOf(PropTypes.object),
};

Footer.defaultProps = {
  menu: [],
};

export default Footer;
