import styled from 'styled-components';
import { Box } from '../../styles/Flex';

const SHero = styled(Box)`
  position: absolute;
  top: 0;
  left: 0;
  z-index: -1;
  height: 100vh;
  width: 100vw;

  h1 {
    text-transform: uppercase;
    font-weight: 400;
  }

  img {
    display: block;
    height: 100%;
    width: 100%;
    object-fit: cover;
  }
`;

export default SHero;
