import React from 'react';
import PropTypes from 'prop-types';

// Styling
import { Flex, FormLabel } from '../../styles';
import STextInput, { SError } from './styles';

const TextInput = ({ label, id, isRequired, errorMessage, width, register, errors, ...props }) => (
  <Flex
    mb={[15, 30]}
    mt={[15, 0]}
    px={[0, 25]}
    flexDirection="column"
    width={width}
    t={console.log(errors)}
  >
    {label && (
      <Flex alignItems="center">
        <FormLabel htmlFor={id}>{isRequired ? label + '*' : label}</FormLabel>
      </Flex>
    )}
    <STextInput
      id={id}
      ref={register({
        required: isRequired && errorMessage.required,
      })}
      {...props}
    />
    {errors && <SError>{errorMessage.required}</SError>}
  </Flex>
);

TextInput.propTypes = {
  label: PropTypes.string,
  id: PropTypes.string.isRequired,
  isRequired: PropTypes.bool,
  errorMessage: PropTypes.object,
  width: PropTypes.oneOfType([PropTypes.number, PropTypes.array]),
  register: PropTypes.func.isRequired,
  errors: PropTypes.oneOfType([PropTypes.object, PropTypes.bool]),
};

TextInput.defaultProps = {
  label: '',
  isRequired: false,
  errorMessage: {},
  width: [1, 1 / 2],
  errors: false,
};

export default TextInput;
