import React from 'react';
import PropTypes from 'prop-types';
import { Box, Text } from '../styles';
import CookieBar from '../CookieBar';

const Footer = ({ options, menus, cookie }) => (
  <Box as="footer">
    {cookie && cookie.show && cookie.text && (
      <CookieBar text={cookie.text} btnText={cookie.btnText} />
    )}
  </Box>
);

Footer.propTypes = {
  options: PropTypes.oneOfType([PropTypes.object, PropTypes.bool, PropTypes.array]).isRequired,
};

Footer.defaultProps = {
  menus: false,
};

export default Footer;
