import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';

// Styling
import { Flex, FormLabel } from '../../styles';
import STextInput, { SError } from './styles';

const TextInput = ({ label, id, isRequired, errorMessage, ...props }) => {
  const [touched, setTouched] = useState(false);

  return (
    <Flex width={1} px={25} flexDirection="column">
      {label && (
        <Flex alignItems="center">
          <FormLabel htmlFor={id}>{isRequired ? label + '*' : label}</FormLabel>
        </Flex>
      )}
      <STextInput id={id} onBlur={() => errorMessage && !touched && setTouched(true)} {...props} />
      {errorMessage && touched && <SError>{errorMessage.required}</SError>}
    </Flex>
  );
};

TextInput.propTypes = {
  label: PropTypes.string,
  id: PropTypes.string.isRequired,
  isRequired: PropTypes.bool,
};

TextInput.defaultProps = {
  label: '',
  isRequired: false,
};

export default TextInput;
