import React from 'react';
import PropTypes from 'prop-types';

// Styling
import { Box, Flex, Text } from '../styles';

const Tile = ({ title, image }) => (
  <Box>
    {image && <img src={`../../../static/images/${image}`} />}
    <Text>{title}</Text>
  </Box>
);

Tile.propTypes = {
  title: PropTypes.string.isRequired,
};

export default Tile;
