import React from 'react';
import PropTypes from 'prop-types';

// Styling
import { Text } from '../styles';
import STile from './styles';
import theme from '../../theme';

const Tile = ({ title, client, image, link }) => (
  <STile>
    <a href={link} target="_blank">
      {image && <img src={`../../../static/images/${image}`} />}
      {client && (
        <Text as="span" mt={20} fontSize={15} fontWeight={700} color={theme.colors.gray}>
          {client}
        </Text>
      )}
      <Text as="h3" mt={10} fontSize={30} fontWeight={400} color={theme.colors.black}>
        {title}
      </Text>
    </a>
  </STile>
);

Tile.propTypes = {
  title: PropTypes.string.isRequired,
};

export default Tile;
