import React from 'react';
import PropTypes from 'prop-types';

// Components
import Tile from '../Tile';

// Styling
import { Flex } from '../../styles';

const Tiles = ({ items }) => (
  <Flex flexWrap="wrap">
    {items?.map(item => (
      <Tile width={1 / 2} {...item} />
    ))}
  </Flex>
);

Tiles.propTypes = {
  items: PropTypes.arrayOf(PropTypes.object).isRequired,
};

export default Tiles;
