import config from '../theme.config';

export default (url, postType) => {
  const result = {};
  if (url && typeof url === 'string') {
    const split = url.replace(/^\/+|\/+$/g, '').split('/');
    if (postType) {
      result.type = postType;
      result.slug = split[split.length - 1];
      return result;
    }
    const [first] = split;
    if (split.length === 1) {
      result.type = 'page';
      result.slug = first;
    } else if (split.length > 1) {
      if (first === config.wordpress.slugs.post) {
        result.type = 'post';
        result.slug = split[split.length - 1];
      } else {
        result.type = 'page';
        result.slug = split[split.length - 1];
      }
    }
  }
  return result;
};
