import styled, { css } from 'styled-components';
import { Box, Text } from '../../styles/Flex';
import { px2rem } from '../../../utils';
import theme from '../../../theme';

const SHero = styled(Box)`
  position: absolute;
  top: 0;
  left: 0;
  height: 100vh;
  width: 100vw;

  img {
    display: block;
    height: 100%;
    width: 100%;
    object-fit: cover;
  }
`;

export default SHero;
