import styled from 'styled-components';
import { Box } from '../styles';
import { px2rem } from '../../utils';

const STile = styled(Box)`
  a {
    text-decoration: none;

    span {
      display: block;
      text-transform: uppercase;
    }

    img {
      display: block;
      width: 100%;
      max-height: ${px2rem(500)};
    }
  }
`;

export default STile;
