import axios from 'axios';
import { parseCookies } from 'nookies';
import config from '../theme.config';

const getPreview = async (ctx) => {
  let data = false;
  let errorCode = false;
  const cookies = parseCookies(ctx);
  try {
    const backCookie = Object.keys(cookies)
      .map((key) => `${key}=${cookies[key]}`)
      .join('; ');
    const { query } = ctx;
    const { id, wpnonce } = query;
    const previewData = await axios.get(
      `${config.wordpress.routes.preview}?id=${id}&_wpnonce=${wpnonce}`,
      {
        headers: {
          Cookie: backCookie,
        },
      }
    );
    data = previewData ? previewData.data : false;
  } catch (error) {
    errorCode = error.message;
  }
  console.log(data, errorCode);
  return { data, errorCode };
};

export default getPreview;
